import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FaEdit, FaTrash } from 'react-icons/fa';
import API_BASE_URL from '../../config';

export default function Utilizadores() {
    const [utilizadoresData, setUtilizadoresData] = useState([]);

    useEffect(() => {
        // Função para buscar os dados dos utilizadores da API
        const fetchUtilizadores = async () => {
            try {
                const response = await fetch(API_BASE_URL + 'utilizador/'); // URL da sua API
                const data = await response.json();
                setUtilizadoresData(data);
            } catch (error) {
                console.error('Erro ao buscar dados dos utilizadores:', error);
            }
        };

        fetchUtilizadores();
    }, []);

    return (
        <div className="container mt-5">
            <h2 className="mb-4">Utilizadores</h2>
            <div className="table-responsive">
                <table className="table align-middle">
                    <thead className="table-light">
                        <tr>
                            <th scope="col"></th>
                            <th scope="col">Colaborador</th>
                            <th scope="col">Email</th>
                            <th scope="col">Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {utilizadoresData.map((usuario) => (
                            <tr key={usuario.id_user}>
                                <td>
                                    <img src={usuario.imagem || 'https://via.placeholder.com/50'} alt="User" className="rounded-circle" width="50" height="50" />
                                </td>
                                <td>{usuario.nome}</td>
                                <td>{usuario.email}</td>
                                <td>
                                    <button className="btn p-1 me-2" style={{ color: 'blue' }}>
                                        <FaEdit size={20} />
                                    </button>
                                    <button className="btn p-1" style={{ color: 'red' }}>
                                        <FaTrash size={20} />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
