import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FaTrash, FaEdit } from 'react-icons/fa';

export default function Noticias() {
    const [noticias, setNoticias] = useState([]);
    const [formValues, setFormValues] = useState({
        titulo: '',
        descricao: '',
        imagem: ''
    });
    const [editing, setEditing] = useState(false);
    const [currentId, setCurrentId] = useState(null);

    // Carregar as notícias da API
    useEffect(() => {
        loadNoticias();
    }, []);

    const loadNoticias = async () => {
        try {
            const response = await axios.get('/api/noticias');
            setNoticias(response.data);
        } catch (error) {
            console.error('Erro ao carregar notícias:', error);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormValues({ ...formValues, [name]: value });
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        if (editing) {
            try {
                await axios.put(`/api/noticias/${currentId}`, formValues);
                setEditing(false);
                setCurrentId(null);
            } catch (error) {
                console.error('Erro ao atualizar notícia:', error);
            }
        } else {
            try {
                await axios.post('/api/noticias', formValues);
            } catch (error) {
                console.error('Erro ao criar notícia:', error);
            }
        }
        setFormValues({ titulo: '', descricao: '', imagem: '' });
        loadNoticias();
    };

    const handleEditClick = (noticia) => {
        setEditing(true);
        setCurrentId(noticia.id_noticia);
        setFormValues({
            titulo: noticia.titulo,
            descricao: noticia.descricao,
            imagem: noticia.imagem
        });
    };

    const handleDeleteClick = async (id_noticia) => {
        try {
            await axios.delete(`/api/noticias/${id_noticia}`);
            loadNoticias();
        } catch (error) {
            console.error('Erro ao deletar notícia:', error);
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">Notícias</h2>

            {/* Formulário para adicionar/editar notícias */}
            <form onSubmit={handleFormSubmit} className="mb-4">
                <div className="mb-3">
                    <label className="form-label">Título</label>
                    <input
                        type="text"
                        className="form-control"
                        name="titulo"
                        value={formValues.titulo}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Descrição</label>
                    <textarea
                        className="form-control"
                        name="descricao"
                        value={formValues.descricao}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Imagem (URL)</label>
                    <input
                        type="text"
                        className="form-control"
                        name="imagem"
                        value={formValues.imagem}
                        onChange={handleInputChange}
                    />
                </div>
                <button type="submit" className="btn btn-primary">
                    {editing ? 'Atualizar Notícia' : 'Adicionar Notícia'}
                </button>
            </form>

            <div className="table-responsive">
                <table className="table align-middle">
                    <thead className="table-light">
                        <tr>
                            <th scope="col"></th>
                            <th scope="col">Título</th>
                            <th scope="col">Descrição</th>
                            <th scope="col">Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {noticias.map((noticia) => (
                            <tr key={noticia.id_noticia}>
                                <td>
                                    <img
                                        src={noticia.imagem}
                                        alt="Imagem da Notícia"
                                        className="img-thumbnail"
                                        width="100"
                                        height="100"
                                    />
                                </td>
                                <td>{noticia.titulo}</td>
                                <td>{noticia.descricao}</td>
                                <td>
                                    <button
                                        className="btn p-1 me-2"
                                        style={{ color: 'blue' }}
                                        onClick={() => handleEditClick(noticia)}
                                    >
                                        <FaEdit size={20} />
                                    </button>
                                    <button
                                        className="btn p-1"
                                        style={{ color: 'red' }}
                                        onClick={() => handleDeleteClick(noticia.id_noticia)}
                                    >
                                        <FaTrash size={20} />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
